            <div className="search-box">
                <input type="text" id="searchInput" className="search-input" placeholder="Enter your search term..."/>
                    <button className="search-button" onClick={handleSearchButtonClick}>Search</button>
            </div>


            {/* <Stack
              sx={{ pt: 4 }}
              direction="row"
              spacing={2}
              justifyContent="center"
            >
              
              
              <Button variant="contained">Main call to action</Button>
              <Button variant="outlined">Secondary action</Button>
            </Stack> */}



          </Container>
        </Box>





        <p align='center' h2 = 'text' >
        Top Sold products....
        </p>  


        <Container sx={{ py: 8 }} maxWidth="md">
          {/* End hero unit */}
        <Grid container spacing={4}>
            {cardsForTopSoldProducts.map((card) => (
              <Grid item key={card} xs={12} sm={6} md={4}>
                <Card
                  sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                >
                  <CardMedia
                    component="div"
                    sx={{
                      // 16:9
                      pt: '56.25%',
                    }}

                    image = {card.PICTURE}
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5" component="h1">
                      {card.P_NAME}<br/>
                      Price: $ {card.PRICE} <br/>
                      {card.DISCOUNT? <p>Discount: {card.DISCOUNT}%</p>:null}
                    </Typography>
                    <Typography>

                    </Typography>
                    
                  </CardContent>
                  <CardActions>
                    <Button size="small" onClick={()=>handleDetailsButtonClick(card.P_ID)}>Details</Button>
                    <Button size="small" onClick={handleOrderButtonClick}>Order Now</Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>





        <p align='center' h2 = 'text'>
        Top rated products.... 
          </p>

        <Container sx={{ py: 8 }} maxWidth="md">
          {/* End hero unit */}
        <Grid container spacing={4}>
            {cardsForTopRatedProducts.map((card) => (
              <Grid item key={card} xs={12} sm={6} md={4}>
                <Card
                  sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                >
                  <CardMedia
                    component="div"
                    sx={{
                      // 16:9
                      pt: '56.25%',
                    }}
                    image = {card.PICTURE}
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5" component="h2">
                      {card.P_NAME}<br/>
                      Price: ${card.PRICE} <br/>
                      {card.DISCOUNT? <p>Discount: {card.DISCOUNT}%</p>:null} 
                    </Typography>
                    <Typography>
                    </Typography>
                    
                  </CardContent>
                  <CardActions>
                    <Button size="small" onClick={()=>handleDetailsButtonClick(card.P_ID)}>Details</Button>
                    <Button size="small" onClick={handleOrderButtonClick}>Order Now</Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>

       

      </main>



      {/* Footer */}
      <Box sx={{ bgcolor: 'background.paper', p: 6 }} component="footer">
        <Typography variant="h6" align="center" gutterBottom>
          For any necessary information, please contact us : 01700000000
        </Typography>
        <Typography
          variant="subtitle1"
          align="center"
          color="text.secondary"
          component="p"
        >
         Lets start with our products.....
        </Typography>
        <Copyright />
      </Box>
      {/* End footer */}

      
   </ThemeProvider>
  );
 
}
